import React from 'react';
import './stylesheet.css';
import PartnerLogo1 from "./images/section_images/partner_logo_1.png";
import { db, auth } from './firebase';
import { Link } from 'react-router-dom';



class MenuBox extends React.Component {
    state = {
        category: null,
    }

    componentDidMount() {
        console.log('mounted')
        db.collection('restaurants').doc(`${this.props.id}`).collection("categories")
            .get()
            .then(snapshot => {
                const category = []
                snapshot.forEach(doc => {
                    const data = doc.data()
                    category.push(data)
                })
                this.setState({ category: category })
                console.log(category)
            })
            .catch(error => console.log(error))

        db.collection('restaurants')
            .get()
            .then(snapshot => {
                const restaurants = []
                snapshot.forEach(doc => {
                    const data = doc.data()
                    data["id"] = doc.id
                    data["categories"] = doc.id.categories
                    restaurants.push(data)
                })
                this.setState({ restaurants: restaurants })
                console.log(restaurants)
            })
            .catch(error => console.log(error))
    }

    render() {
        return (
            <div>
                {
                    this.state.restaurants &&
                    this.state.restaurants.flatMap(category => {
                        return (
                            <a>
                                <Link to={{
                                    pathname: `/restaurants/${this.props.id}/${category.name}`,
                                    state: {
                                        name: `${restaurant.name}`,
                                        city: `${restaurant.city}`,
                                        state: `${restaurant.state}`,
                                        cuisine: `${restaurant.cuisine}`,
                                        street: `${restaurant.street}`,
                                        zip: `${restaurant.zip}`,
                                        phone: `${restaurant.phone}`,
                                        id: `${restaurant.id}`,
                                        logo_url: `${restaurant.logo_url}`,
                                        storeFrontImage_url: `${restaurant.storeFrontImage_url}`
                                    }
                                }}>
                                    <div class="menu-category" style={{ backgroundImage: "url(" + category.image_url + ")" }}>
                                        <div class="menu-category-info">
                                            <h3>{`${category.name}`}</h3>

                                        </div>
                                    </div>
                                </Link>
                            </a>
                        )
                    })
                }
            </div >
        )
    }
}


export default MenuBox;
