import React from 'react';
import './stylesheet.css';
import PartnerLogo1 from "./images/section_images/partner_logo_1.png";
import { db, auth } from './firebase';
import MenuBox from './menuBox'
import Restaurants from './restaurantBox'

class MenuItem extends React.Component {

    state = {
        category: null,
    }

    componentDidMount() {
        console.log('mounted')
        db.collection('restaurants').doc(`${this.props.location.state.id}`).collection("categories")
            .get()
            .then(snapshot => {
                const category = []
                snapshot.forEach(doc => {
                    const data = doc.data()
                    category.push(data)
                })
                this.setState({ category: category })
                console.log(category)
            })
            .catch(error => console.log(error))
    }

    render() {
        return (
            <div>

                <a>
                    <div class="menu-item">
                        <div class="menu-item-info">
                            <div class="menu-item-top">
                                <h3>Dish</h3>
                                <p>200 calories</p>
                            </div>
                            <p>SeeFood is a mobile website that helps you see pictures.</p>
                        </div>
                    </div>
                </a>

            </div>
        )
    }




}

export default MenuItem;
