import React from 'react';
import './stylesheet.css';
import PartnerLogo1 from "./images/section_images/partner_logo_1.png";
import { db, auth } from './firebase';
import MenuBox from './menuBox'
import MenuItem from './menu-item'
import Restaurants from './restaurantBox'


class SubMenu extends React.Component {

    state = {
        category: null,
    }

    componentDidMount() {
        console.log('mounted')
        db.collection('restaurants').doc(`${this.props.id}`).collection("categories")
            .get()
            .then(snapshot => {
                const category = []
                snapshot.forEach(doc => {
                    const data = doc.data()
                    category.push(data)
                })
                this.setState({ category: category })
                console.log(category)
            })
            .catch(error => console.log(error))
    }

    render() {
        return (
            <div>
                {
                    this.state.category &&
                    this.state.category.flatMap(category => {
                        return (
                            <div class="menu-items-grid">
                                <MenuItem to={{
                                    state: {
                                        name: `${category.name}`,
                                        categories: `${category.categories[0].items[0].name}`
                                    }
                                }} />
                                <h1>{this.props.location.state.categories}</h1>
                            </div>
                        )
                    })
                }
            </div >
        )
    }
}


export default SubMenu;
